const Int MOD = 1e9+7;

Int calc(Int n,vector<Int> a){
  vector<Int> c(5,0),s(5,0);
  for(Int i=0;i<n;i++){
    c[to_string(a[i]).size()]++;
    s[to_string(a[i]).size()]+=a[i];
  }

  auto P=make_v<Int>(n+1,n+1);
  fill_v(P,0);
  for(Int i=0;i<=n;i++){
    for(Int j=0;j<=i;j++){
      P[i][j]=1;
      for(Int k=0;k<j;k++)
	P[i][j]=P[i][j]*(i-k)%MOD;
    }
  }
  vector<Int> dp2(n+1,0);
  for(Int i=0;i<=n;i++){
    for(Int j=0;j<=i;j++){
      dp2[i]+=P[i][j];
      dp2[i]%=MOD;
    }
  }

  vector<Int> fact(n+1,1);
  for(Int i=0;i<n;i++) fact[i+1]=fact[i]*(i+1)%MOD;
  vector<Int> po(n*4+1,1);
  for(Int i=0;i<n*4;i++) po[i+1]=po[i]*10%MOD;

  Int ans=0;
  for(Int d=1;d<=4;d++){
    if(!c[d]) continue;
    c[d]--;
    auto dp=make_v<Int>(n+1,n*4+1);
    fill_v(dp,0);
    dp[0][0]=1;
    for(Int a=1;a<=4;a++){
      for(Int x=0;x<c[a];x++){
	for(Int i=n;i>=0;i--){
	  for(Int j=n*4;j>=0;j--){
	    Int ni=i+1,nj=j+a;
	    if(ni<=n&&nj<=n*4){
	      dp[ni][nj]+=dp[i][j];
	      dp[ni][nj]%=MOD;
	    }
	  }
	}
      }
    }
    c[d]++;
    for(Int i=0;i<n;i++){
      for(Int j=0;j<=4*n;j++){
	Int tmp=dp[i][j]*fact[i]%MOD;
	Int k=n-(i+1);
	tmp*=dp2[k];	
	tmp%=MOD;

	tmp*=po[j];
	tmp%=MOD;

	tmp*=s[d];
	tmp%=MOD;

	ans+=tmp;
	ans%=MOD;

	if(0&&dp[i][j])
	  cout<<i<<":"<<dp[i][j]<<" "<<dp2[k]<<" "<<po[j]<<" "<<s[d]<<":"<<tmp<<endl;	
      }
    }
  }
  return ans;
}

signed main(){  
  cin.tie(0);
  ios::sync_with_stdio(0);
  Int n;
  cin>>n;
  vector<Int> a(n);
  for(Int i=0;i<n;i++) cin>>a[i];
  sort(a.rbegin(),a.rend());
  Int ans=calc(n,a);
  //cout<<ans<<endl;
  if(a.back()==0){
    a.pop_back();
    n--;
    ans+=MOD-calc(n,a);
    ans%=MOD;
  }
  cout<<ans<<endl;
  return 0;
}

