		F[x] = 0;
		G[x] = 0;
		ll a = 1;
		rep(i,x+1){
			F[x] += a;
			G[x] += a*(i+1);
			if(i < x){ a *= x-i; a %= M; }
		}
		F[x] %= M;
		G[x] %= M;
	}
}

