Mod rec(int sz, int i, int keta, int used, const vi& v)
{
	if (dp[sz][i][keta][used] >= 0) return dp[sz][i][keta][used];
	Mod res = 0;
	if (i == 5)
	{
		if (keta == 0)
		{
			Mod tmp = fact<mod>(used);
			int rem = n - 1 - used;
			REP(j, rem + 1)
			{
				res += tmp * perm<mod>(rem, j);
			}
		}
	}
	else
	{
		REP(j, v[i] + 1)
		{
			if (keta < i * j) break;
			res += rec(sz, i + 1, keta - i*j, used + j, v) * comb<mod>(v[i], j);
		}
	}
	return dp[sz][i][keta][used] = res;
}

