	      dp[ni][nj]+=dp[i][j];
	      dp[ni][nj]%=MOD;
	    }
	  }
	}
      }
    }
    c[d]++;
    for(Int i=0;i<n;i++){
      for(Int j=0;j<=4*n;j++){
	Int tmp=dp[i][j]*fact[i]%MOD;
	Int k=n-(i+1);
	tmp*=dp2[k];	
	tmp%=MOD;

	tmp*=po[j];
	tmp%=MOD;

	tmp*=s[d];
	tmp%=MOD;

	ans+=tmp;
	ans%=MOD;

	if(0&&dp[i][j])
	  cout<<i<<":"<<dp[i][j]<<" "<<dp2[k]<<" "<<po[j]<<" "<<s[d]<<":"<<tmp<<endl;	
      }
    }
  }
  return ans;
}

