    static vector<ll> k(n);
    if(!k[0]){k[0]=1;for(int i=1;i<(int)k.size();i++)k[i]=i*k[i-1]%mod;}
    return k[i];
  }


  ll nPr(ll n, ll r){return K(n) * inv(K(r)) % mod;}

  //O(log(mod))  k,r
  ll nCr(ll n,ll r){
    ll a = inv( K(r) * K(n-r) % mod);
    return K(n) * a % mod;
  }

  //O(r + log(mod))
  ll nCr2(ll n,ll r){
    ll N = 1, K = 1; 
    for(int i=0;i<r;i++) N=N*((n-i)%mod)%mod;
    for(int i=0;i<r;i++) K=K*((r-i)%mod)%mod;
    ll R = inv(K);
    return N*R%mod;
  }

  /**/
  static ll carmichaelLambda(ll n){
    ll res = 1;
    if(n % 8 == 0) n/=2;
    auto lcm = [](ll a,ll b){return (a/__gcd(a,b)) * b;};

    auto compute = [&](ll i){
      ll sub = i - 1;
      n/=i;
      while(n%i == 0) n/=i, sub *=i;
      res = lcm(res,sub);
    };

    for(int i=2;i*i<=n;i++) if( n % i == 0) compute(i);
    if(n != 1) compute(n);
    return res;
  }

  //n  
  static vector<int> getPrime(int n){
    vector<bool>used(n+1,0);

    for(ll i=2;i*i<=n;i++)
      if(!used[i]) for(int j=2;j<=n/i;j++) used[i*j]=1;

    vector<int> res;
    for(int i=2;i<=n;i++)if(!used[i]) res.push_back(i);

    return res;
  }

  //
  static bool isPrime(ll n){
    if(n < 2) return 0;
    for(ll i=2;i*i<=n;i++) if(n%i==0) return 0;
    return 1;
  }

  // x == 0 || x == 1
  static vector<ll> primeFactor(ll x){
    //if(x < 2) return {x}; /*!!!!!!!!!!!!!!!!!!!*/
    assert(x > 1);/*!!!!!!!!!!!!!!!!!!!*/

    vector<ll> res;
    for(ll i=2;i*i <= x;i++)
      while(x%i == 0){
        x/=i;
        res.push_back(i);
      }
    if(x != 1) res.push_back(x);
    return res;
  }

  static map<ll,ll> primeFactorMap(ll x){

    map<ll,ll> res;
    //if(x < 2) {
    //res[x]++;
    //  return res; /*!!!!!!!!!!!!!!!!!!!*/
    //}

    assert(x > 1); /*!!!!!!!!!!!!!!!!!!!*/

    for(ll i=2;i*i <= x;i++)
      while(x%i == 0) x/=i, res[i]++;

    if(x != 1) res[x]++;
    return res;
  }

  static ll divisor_Sum(map<ll,ll> prime){ // first : prime  second : cnt
    ll res = 1;
    for(P p : prime){
      ll sum = 1, mul = p.first;
      for(ll i=0;i<p.second;i++) sum += mul, mul *= p.first;
      res *= sum;
    }
    return res;
  }

  static ll divisor_Sum(ll num){
    return divisor_Sum(primeFactorMap(num));
  }

  //
  static vector<ll> divisor(ll x){
    vector<ll> res;
    for(ll i=1; i*i<=x ;i++)
      if(x%i==0){
        res.push_back(i);
        if(i*i!=x) res.push_back(x/i);
      }
    sort(res.begin(), res.end());
    return res;
  }

  //a * b
  static bool overflow(ll a,ll b){return a > LONG_MAX/b;}

};

const int N = 201;
const int L = 4 * N;
typedef vector<int> V;
typedef vector<V> VV;
typedef vector<VV> VVV;
VV DP(V A){
  Math math;
  VVV dp(5,VV(N,V(L,0))); 
  dp[0][0][0] = 1;
  for(int i=0;i<4;i++){ // 1, 2, 3, 4
    int a = A[i];
    for(int j=0;j<N;j++) // 
      for(int k=0;k<L;k++){ // 
        if(dp[i][j][k] == 0) continue;
        for(int l=0;l<=a;l++){ //
          if(j + l >= N || k + (i+1) * l >= L) continue;
          int &ndp = dp[i+1][j + l][k + (i+1) * l];
          ndp += dp[i][j][k] * math.nCr(a,l) % mod;
          ndp %= mod;
        }
      }
  }
  return dp[4];
}

int calc1(int n){
  static Math math;
  static vector<int> used(N),mem(N);
  if(used[n]++) return mem[n];
  return mem[n] = math.K(n);
}

int calc2(int n){
  static Math math;
  static vector<int> used(N),mem(N);
  if(n == 0) return 1;
  if(n <= 0) return 0;
  if(used[n]++) return mem[n];
  int res = 0;
  for(int i=0;i<=n;i++){
    res += math.nPr(n,i);
    res %= mod;
  }
  return mem[n] = res;
}

int solve(vector<int> A){
  int n = A.size();
  int res = 0;
  Math math;
  vector<int> X(4,0);
  int sum[4] = {};
  for(int i=0;i<n;i++) {
    int len = to_string(A[i]).size();
    X[len-1]++;
    sum[len-1] = (sum[len-1] + A[i]) % mod;
  }

  for(int k=0;k<4;k++){
    if(X[k] == 0) continue;
    X[k]--;
    VV dp = DP(X);

    for(int i=0;i<N;i++)
      for(int j=0;j<L;j++){
        int a = dp[i][j] * calc1(i) % mod; //
        a = a * calc2(n-i - 1) % mod;
        int b = sum[k] * math.mod_pow(10,j) % mod;//
        res = (res + a * b) % mod;
      }
    X[k]++;
  }
  return res;
}

signed main(){
  cin.tie(0);
  ios_base::sync_with_stdio(0);
  cout << fixed << setprecision(12);

  int n;
  cin>>n;
  vector<int> A(n);
  cin>>A;
  sort(A.begin(),A.end());

  int ans = solve(A);
  if(A[0] == 0) {
    A.erase(A.begin());
    ans = ans + mod - solve(A);
    ans %= mod;
  }
  cout<<ans<<endl;
  return 0;
}

