#include <bits/stdc++.h>
#define rep(i,n) for (int i = 0; i < n; i++)
using namespace std;
typedef long long ll;
typedef unsigned long long ull;
typedef pair<int, int> pi;
typedef pair<ll, ll> pl;
const double EPS = 1e-9;
const ll mod = 1000000007;
const int inf = 1 << 30;
const ll linf = 1LL << 60;
const double PI = 3.14159265358979323846;

int n;
ll L;
ll a[200];
ll l[200];
ll S[201];
ll A[5];
ll kai[201], p10[801];
ll conb[201][201];
ll dp[5][201][801];

void init() {
    kai[0] = 1;
    for (ll i = 1; i <= 200; i++) kai[i] = kai[i-1]*i%mod;
    p10[0] = 1;
    for (ll i = 1; i <= 800; i++) p10[i] = p10[i-1]*10%mod;
    conb[0][0] = 1;
    for (int i = 1; i <= 200; i++) {
        for (int j = 0; j <= i; j++) {
            conb[i][j] = conb[i-1][j];
            if (j > 0) conb[i][j] = (conb[i][j]+conb[i-1][j-1])%mod;
        }
    }
    rep(i,201) {
        S[i] = 0;
        rep(j,i+1) {
            S[i] = (S[i] + conb[i][j]*kai[j])%mod;
        }
    }
}

ll solve1() {
    rep(i,n) l[i] = to_string(a[i]).size();
    L = 0;
    rep(i,n) L += l[i];
    memset(A, 0, sizeof(A));
    rep(i,n) A[l[i]] += a[i];

    memset(dp, 0, sizeof(dp));
    for (int i = 1; i <= 4; i++) {
        //if (A[i] == 0) continue;
        dp[i][0][0] = 1;
        bool skip = false;
        rep(j,n) {
            if (l[j] == i && !skip) {
                skip = true;
                continue;
            }
            for (int k = n; k >= 1; k--) {
                for (int t = L; t >= l[j]; t--) {
                    dp[i][k][t] = (dp[i][k][t]+dp[i][k-1][t-l[j]])%mod;
                }
            }
        }
        rep(k,n) rep(t,L) dp[i][k][t] %= mod;
    }

/*
    rep(i,n) {
        rep(j,L) {
            cerr << dp[1][i][j] << " ";
        }
        cerr << endl;
    }
*/

    ll ret = 0;
    for (int i = 1; i <= 4; i++) {
        ll tmp = 0;
        rep(j,n) {
            rep(k,L) {
                ll tmp2 = dp[i][j][k]*p10[k]%mod;
                tmp2 = tmp2 * S[n-j-1] % mod;
                tmp2 = tmp2 * kai[j] % mod;
                tmp = (tmp + tmp2) % mod;
            }
        }
        ret = (ret + A[i] * tmp) % mod;
    }
    return ret;
}

void solve() {
    init();
    cin >> n;
    rep(i,n) cin >> a[i];
    sort(a, a+n);
    ll ans = solve1();
    if (a[0] > 0) {
        cout << ans << endl;
        return;
    }
    a[0] = linf;
    sort(a, a+n); n--;
    ans = (ans - solve1() + mod) % mod;
    cout << ans << endl;
}

