        {
            if (a[i] != 0)
            {
                _a.push_back(a[i]);
                _d.push_back(d[i]);
            }
        }

        ll sub = gao(_a, _d);
        res = (res - sub + mod) % mod;
    }

    cout << res << endl;
}

