	{
		string s = to_string(a[i]);
		REP(j, 801)
		{
			Mod cal = calc(s.size(), j);
			Mod t = 0;
			REP(k, s.size())
			{
				Mod tmp = Mod(s[k] - '0') * pow(Mod(10), j + s.size() - k - 1) * cal;
				ans += tmp;
				t += tmp;
			}
			//cout << j <<" "<< cal <<" * " << a[i] * pow(Mod(10), j) << " =" <<t<<" "<<ans << endl;
		}
	}

	Mod zero = 0;
