int a[222];
LL s[4];
LL cnt[4];
LL fact[222];
LL invfact[222];
LL powten[1000];
LL f[222];

LL extgcd(LL a, LL b, LL &x, LL &y){
	LL d = a;
	if(b){
		d = extgcd(b, a % b, y, x);
		y -= a/b * x;
	}
	else x = 1, y = 0;
	return d;
}

LL mod_inverse(LL a, LL m){
	LL x, y;
	extgcd(a, m, x, y);
	return (m + x%m) % m;
}

inline LL P(int n, int r){
	if(n<=0)return 0LL;
	return (fact[n]*invfact[n-r])%MOD;
}

inline LL C(int n, int r){
	if(n<0)return 0LL;
	return (P(n,r)*invfact[r])%MOD;
}
LL solve(int n){
	LL ret=0;
	int loop[4];
	for(loop[0]=0; loop[0]<=cnt[0]; loop[0]++){
		for(loop[1]=0; loop[1]<=cnt[1]; loop[1]++){
			for(loop[2]=0; loop[2]<=cnt[2]; loop[2]++){
				for(loop[3]=0; loop[3]<=cnt[3]; loop[3]++){
					int sum1 = loop[0]+loop[1]+loop[2]+loop[3];
					int sum2 = loop[0]+2*loop[1]+3*loop[2]+4*loop[3];
					LL tmp = 0;
					REP(i,4){
						LL tmp2=0;
						REP(j,4){
							tmp2 += C(cnt[j]-(i==j), loop[j]);
							tmp2 %= MOD;
						}
						tmp += tmp2*s[i];
						tmp %= MOD;
					}
					if(n-sum1-1<0)tmp=0;
					else tmp *= f[n-sum1-1];
					tmp %= MOD;
					tmp *= fact[sum1];
					tmp %= MOD;
					tmp *= powten[sum2];
					tmp %= MOD;
					ret = (ret+tmp)%MOD;
				}
			}
		}
	}
	return ret;
}

int main(){
	fact[0]=fact[1]=invfact[0]=invfact[1]=1;
	FOR(i,2,222)fact[i]=(fact[i-1]*i)%MOD;
	FOR(i,2,222)invfact[i]=mod_inverse(fact[i], MOD);
	powten[0]=1;
	FOR(i,1,1000)powten[i] = (powten[i-1]*10)%MOD;
	f[0]=1;
	FOR(i,1,222){
		f[i]=1;
		FOR(j,1,i+1)f[i]=(f[i]+P(i,j))%MOD;
	}
	int n;
	cin>>n;
	REP(i,n)scanf("%d", a+i);
	sort(a,a+n);
	int c=10;
	REP(i,n){
		REP(j,4){
			if(a[i]<c){
				cnt[j]++;
				s[j]+=a[i];
				break;
			}
			c*=10;
		}
	}
	LL ret = solve(n);
	if(a[0]==0){
		cnt[0]--;
		ret -= solve(n-1);
	}
	while(ret<0)ret+=MOD;
