		sums[keta]+=a;
	}
	Mod ans = 0;
	for (int keta = 1; keta <= 4; ++keta) {
		ketas[keta]--;
		vector<int>under_nums(5);

		for (under_nums[1] = 0; under_nums[1] <= ketas[1]; ++under_nums[1]) {
