					for (under_nums[4] = 0; under_nums[4] <= ketas[4]; ++under_nums[4]) {
						Mod under_choice = 1;
						int under_size = 0;
						for (int i = 1; i <= 4; ++i) {
							under_size += under_nums[i] * i;
							under_choice *= comb(ketas[i], under_nums[i]);
						}
						int under_count = accumulate(under_nums.begin(), under_nums.end(), 0);
						Mod under_perm =fact[under_count];

						int above_sum = accumulate(ketas.begin(), ketas.end(), 0) - accumulate(under_nums.begin(), under_nums.end(), 0);
						Mod above_choice = memo[above_sum];
						ans += Mod(sums[keta]) * under_choice*under_perm*above_choice*mod_pow(Mod(10),under_size);
					}
				}
			}
		}
		ketas[keta]++;
	}
	if (have_zero) {
		ketas[1]--;
		for (int keta = 1; keta <= 4; ++keta) {
			ketas[keta]--;
