const int MOD = 1000000007;

void combination(int n, vector<vector<long long> >& comb)
{
    comb.assign(n+1, vector<long long>(n+1, 0));
    for(int i=0; i<=n; ++i){
        comb[i][0] = 1;
        for(int j=1; j<=i; ++j){
            comb[i][j] = comb[i-1][j-1] + comb[i-1][j];
            comb[i][j] %= MOD;
        }
    }
}

int main()
{
    vector<vector<long long> > comb; // 
    combination(1000, comb);

    vector<long long> factorial(1001, 1); // 
    for(int i=1; i<=1000; ++i)
        factorial[i] = (factorial[i-1] * i) % MOD;

    vector<long long> pow10(1001, 1); // 10
    for(int i=1; i<=1000; ++i)
        pow10[i] = (pow10[i-1] * 10) % MOD;

    vector<long long> select(1001, 1); // [i] : ii
    for(int i=1; i<=1000; ++i){
        for(int j=1; j<=i; ++j){
            select[i] *= j;
            ++ select[i];
            select[i] %= MOD;
        }
    }

    //////////////////////////////////////////////////////////////////////

    int n;
    cin >> n;

    vector<int> x(5);   // [i] : i
    vector<int> sum(5); // [i] : i
    for(int i=0; i<n; ++i){
        int a;
        cin >> a;

        int b = a;
        int len = 0;
        while(b > 0){
            ++ len;
            b /= 10;
        }
        ++ x[len];
        sum[len] += a;
    }

    int m = 1;
    for(int i=0; i<5; ++i)
        m *= x[i] + 1;

    long long ret = 0;
    for(int i=0; i<m-1; ++i){
        int tmp = i;
        vector<int> y(5);
        int num = 0;
        int len = 0;
        for(int i=0; i<5; ++i){
            y[i] = tmp % (x[i] + 1);
            num += y[i];
            len += y[i] * max(1, i);
            tmp /= (x[i] + 1);
        }

        for(int j=1; j<5; ++j){
            if(x[j] == 0)
                continue;
            if(y[j] + 1 > x[j])
                continue;

            long long ret2 = 1;
            for(int k=0; k<5; ++k){
                if(j == k)
                    ret2 *= comb[x[k]-1][y[k]];
                else
                    ret2 *= comb[x[k]][y[k]];
                ret2 %= MOD;
            }

            ret2 *= factorial[num];
            ret2 %= MOD;

            ret2 *= pow10[len];
            ret2 %= MOD;

            if(x[0] - y[0] > 0){
                ret2 *= (select[n-num-1] - select[n-num-2] + MOD);
                ret2 %= MOD;
            }else{
                ret2 *= select[n-num-1];
                ret2 %= MOD;
            }

            ret += ret2 * sum[j];
            ret %= MOD;
        }
    }

    cout << ret << endl;

    return 0;
}

