int get_digit(ll num){
	if(num == 0)return 1;

	int count = 0;

	while(num > 0){
		count++;
		num /= 10;
	}
	return count;
}

void make_dp(){

	for(int digit = 1; digit <= 4; digit++){
		for(int use_num = 0; use_num < 200; use_num++){
			for(int loc = 0; loc < 1000; loc++)dp[digit][use_num][loc] = 0;
		}
	}

	ll sum_digit,sum_use,tmp;
	int work[5];

	//14[]
	for(int digit = 1; digit <= 4; digit++){ //

		for(int num1 = 0; num1 <= num_count[1]; num1++){ //1
			if(digit == 1 && num1 == 0)continue; //11()

			for(int num2 = 0; num2 <= num_count[2]; num2++){
				if(digit == 2 && num2 == 0)continue;

				for(int num3 = 0; num3 <= num_count[3]; num3++){
					if(digit == 3 && num3 == 0)continue;

					for(int num4 = 0; num4 <= num_count[4]; num4++){
						if(digit == 4 && num4 == 0)continue;

						work[1] = num1;
						work[2] = num2;
						work[3] = num3;
						work[4] = num4;

						work[digit] -= 1; //

						sum_use = 0;
						sum_digit = 0;

						for(int i = 1; i <= 4; i++){
							sum_use += work[i];
							sum_digit += i*work[i];
						}

						//
						tmp = 1;
						for(int i = 1; i <= 4; i++){
							if(digit != i){
								tmp *= nCm[num_count[i]][work[i]];
							}else{ //digit == i
								tmp *= nCm[num_count[i]-1][work[i]];
							}
							tmp %= MOD;
						}
						tmp *= mult_num[sum_use]; //sum_usesum_use!
						tmp %= MOD;

						dp[digit][sum_use][sum_digit] += tmp;
						dp[digit][sum_use][sum_digit] %= MOD;
					}
				}
			}
		}
	}
}

ll calc(){

	make_dp();

	ll ret = 0;
	ll tmp;
	for(int digit = 1; digit <= 4; digit++){
		for(int use_num = 0; use_num < 200; use_num++){
			for(int loc = 0; loc < 1000; loc++){

				tmp = dp[digit][use_num][loc]*POW[loc];
				tmp %= MOD;
				tmp *= mult_sum[N-(use_num+1)]; //
				tmp %= MOD;
				tmp *= digit_sum[digit];
				tmp %= MOD;
				ret += tmp;
				ret %= MOD;
			}
		}
	}
	return ret;
}

