		tmp_sum = a+1; //aP0 == 1,aP1 = a
		number = a;
		for(ll k = 1; k <= a-1; k++){
			number *= (a-k);
			number %= MOD;
			tmp_sum += number;
			tmp_sum %= MOD;
		}
		mult_num[a] = number; //a!
		mult_sum[a] = tmp_sum; //aPk
	}

	//
	nCm[0][0] = 1;
