    Add( DP[a+1][b][c][d][z], mul(DP[a][b][c][d][z], 10*cc )    );
    Add( DP[a+1][b][c][d][z], mul(divi(pp,cnt[1]),sum[1]) );
  }

  if(b<cnt[2]){
    int cc=cnt[2]-b;
    ll pp=mul(dp[a][b][c][d][z], cc);
    Add( dp[a][b+1][c][d][z], pp );
    Add( DP[a][b+1][c][d][z], mul(DP[a][b][c][d][z], 100*cc )    );
    Add( DP[a][b+1][c][d][z], mul(divi(pp,cnt[2]),sum[2]) );
  }
  if(c<cnt[3]){
    int cc=cnt[3]-b;
    ll pp=mul(dp[a][b][c][d][z], cc);
    Add( dp[a][b][c+1][d][z], mul(dp[a][b][c][d][z], cc) );
    Add( DP[a][b][c+1][d][z], mul(DP[a][b][c][d][z], 1000*cc )    );
    Add( DP[a][b][c+1][d][z], mul(divi(pp,cnt[3]),sum[3]) );
  }
  if(d<cnt[4]){
