    Add( DP[a][b][c][d+1][z], mul(divi(pp,cnt[4]),sum[4]) );
  }

  if(z<Z && a+b+c+d>0){
    Add( dp[a][b][c][d][z+1], dp[a][b][c][d][z] );
    Add( DP[a][b][c][d][z+1], mul(DP[a][b][c][d][z], 10 )    );
  }
}

ll solve(){
