ll ten_pow[M];

void add(ll& a, ll b)
{
    a += b;
    if (a >= mod)
        a -= mod;
}
vector<vector<ll> > digits_dp(const vector<int>& digits)
{
    int n = digits.size();

    static ll dp[210][M + 30];
    CL(dp, 0);
    dp[0][0] = 1;
    for (int i = 0; i < n; ++i)
        for (int j = i; j >= 0; --j)
            for (int k = 5 * i; k >= 0; --k)
                add(dp[j + 1][k + digits[i]], dp[j][k]);

    vector<vector<ll> > res(n + 1, vector<ll>(M));
    for (int i = 0; i <= n; ++i)
        for (int j = 0; j < M; ++j)
            res[i][j] = dp[i][j];
    return res;
}
ll gao(const vector<int>& a, const vector<int>& digits)
{
    int n = a.size();

    ll pat[210] = {};
    for (int i = 0; i <= n - 1; ++i)
        for (int j = 0; j <= (n - 1) - i; ++j)
            (pat[i] += (combi[(n - 1) - i][j] * fact[j] % mod) * fact[i]) %= mod;

    ll res = 0;
    for (int i = 0; i < n; ++i)
    {
        vector<int> d;
        for (int j = 0; j < n; ++j)
            if (i != j)
                d.push_back(digits[j]);
        vector<vector<ll> > d_pat = digits_dp(d);

        for (int j = 0; j < n; ++j)
        {
            for (int k = 0; k < M; ++k)
            {
                ll p = pat[j] * d_pat[j][k] % mod;
                (res += ((a[i] * ten_pow[k]) % mod) * p) %= mod;
            }
        }
    }

    return res;
}
