typedef long long ll;
ll mod=1e9+7;

ll add(ll a,ll b){ return (a+b)%mod; }

void Add(ll &a,ll b){
  a=(a+b)%mod;
}

ll mul(ll a,ll b){ return (a*b)%mod; }


ll mpow(ll a,ll b){
  ll res=1;
  while(b){
    if(b%2==1)res=mul(res,a);
    a=mul(a,a);
    b/=2;
  }
  return res;
}

ll divi( ll a,ll b){
  return mul(a, mpow(b,mod-2) );
}

ll mem[1000][1000];

ll nCr(ll n,ll r){
  if(r==0)return 1;
  if(n==r)return 1;
  if(n<r)return 0;

  if(mem[n][r])return mem[n][r];
  mem[n][r]=add( nCr(n-1,r-1) , nCr(n-1,r) );
  return mem[n][r];
}

