#pragma warning(disable : 4996)

#define mod 1000000007

using namespace std;


// ------ 0. Variables, Functions ------ //

int n;

vector<int> fact;
vector<int> inv;
vector<int> factinv;
vector<int> power2;
vector<int> power10;

int digit(int n)
{
	if (n < 10) return 1;

	return digit(n / 10) + 1;
}

int solve(vector<int> a)
{
	vector<vector<int> > d(5, vector<int>(2, 0));

	for (int i = 0; i < n; i++)
	{
		d[digit(a[i])][0] += 1;
		d[digit(a[i])][1] += a[i];
	}

	int ret = 0;

	for (int i = 1; i <= 4; i++)
	{
		vector<vector<vector<int> > > dp(5, vector<vector<int> >(n, vector<int>(n * 4, 0)));

		dp[0][0][0] = 1;

		if (d[i][0] != 0)
		{
			d[i][0]--;

			for (int j = 0; j < 4; j++)
			{
				for (int k = 0; k < n; k++)
				{
					for (int l = 0; l < n * 4; l++)
					{
						if (dp[j][k][l] != 0)
						{
							for (int m = 0; m <= d[j + 1][0]; m++)
							{
								dp[j + 1][k + m][l + (j + 1) * m] += dp[j][k][l];
								dp[j + 1][k + m][l + (j + 1) * m] %= mod;
							}
						}
					}
				}
			}

			d[i][0]++;

			int res = 0;

			int mul = 0;

			for (int j = n - 1; j >= 0; j--)
			{
				mul = (mul + 1LL * fact[n - j - 1] * fact[n - 1] % mod * factinv[n - j - 1] % mod * factinv[j] % mod) % mod;

				for (int k = 0; k < n * 4; k++)
				{
					res = (res + 1LL * dp[4][j][k] * power10[k] % mod * mul % mod) % mod;
				}
			}

			ret = (ret + 1LL * res * d[i][1]) % mod;
		}
	}

	return ret;
}

int main()
{
	// ------ 1. init ------ //

	fact.push_back(1);

	for (int i = 1; i <= 200; i++) fact.push_back(1LL * fact[i - 1] * i % mod);

	power2.push_back(1);

	for (int i = 1; i <= 200; i++) power2.push_back(1LL * power2[i - 1] * 2 % mod);

	power10.push_back(1);

	for (int i = 1; i <= 800; i++) power10.push_back(1LL * power10[i - 1] * 10 % mod);

	inv.push_back(0);
	inv.push_back(1);

	for (int i = 2; i <= 200; i++) inv.push_back(1LL * inv[mod % i] * (mod - mod / i) % mod);

	factinv.push_back(1);

	for (int i = 1; i <= 200; i++) factinv.push_back(1LL * factinv[i - 1] * inv[i] % mod);


	// ------ 2. Input ------ //

	scanf("%d", &n);

	vector<int> a(n);

	for (int i = 0; i < n; i++)
	{
		scanf("%d", &a[i]);
	}

	sort(a.begin(), a.end(), greater<int>());

	int res = solve(a);

	if (a[n - 1] == 0)
	{
		a.pop_back(); n--;

		res += (mod - solve(a));
		res %= mod;
	}

	printf("%d\n", res);

	return 0;
}

#include <bits/stdc++.h>
#define REP(i,n) for(int i=0; i<(int)(n); ++i)

using namespace std;
typedef long long ll;

const int    INF = 1000000000;
const ll     MOD = 1000000007;
const double EPS = 1e-8;

ll memo[210][810];
ll calc(int i, int k, ll dp[210][810], ll len){
    if(i == 0) return (k == 0 ? 1 : 0);
    if(k <  0) return 0;
    if(memo[i][k] != -1) return memo[i][k];
    return memo[i][k] = dp[i][k] - calc(i - 1, k - len, dp, len);
}

ll fact[210], ifact[210];
ll inv[210];
ll pow_10[210];
void init(int N){
    pow_10[0] = 1;
    for(int i = 1; i <= N; i++){
        pow_10[i] = pow_10[i - 1] * 10 % MOD;
    }
    inv[1] = 1;
    for (int i = 2; i <= N; ++i){
        inv[i] = inv[MOD % i] * (MOD - MOD / i) % MOD;
    }
    fact[0] = 1, ifact[0] = 1;
    for(int i = 1; i <= N; i++){
        fact[i] = fact[i - 1] * i % MOD;
        ifact[i] = ifact[i - 1] * inv[i] % MOD;
    }
}
ll C(int n, int r){
    if(n < 0 || r < 0 || r > n) return 0;
    if(r > n / 2) r = n - r;
    return fact[n] * ifact[n - r] % MOD * ifact[r] % MOD;
}
ll solve(vector<ll> a, vector<ll> len){
    int n = a.size();
    ll dp[210][810] = {};
    dp[0][0] = 1;
    for(int i = 0; i < n; i++){
        for(int j = i; j >= 0; j--){
            for(int k = 4 * i; k >= 0; k--){
                dp[j + 1][k + len[i]] += dp[j][k];
                dp[j + 1][k + len[i]] %= MOD;
            }
        }
    }

    ll ways_left[210] = {};
    for(int i = 0; i <= n; i++){
        for(int j = 0; j <= i; j++){
            ways_left[i] += C(i, j) * fact[j] % MOD;
            ways_left[i] %= MOD;
        }
    }
    ll ans = 0;
    REP(i, n){
        REP(j, 210) REP(k, 810) memo[j][k] = -1;
        for(int j = 0; j <= n - 1; j++){
            for(int k = 0; k <= j * 4; k++){
                //printf("(%d, %d, %d) = %lld\n", i, j, k, ways_left[n - 1 - j] * a[i] % MOD * pow_10[k] % MOD * calc(j, k, dp, len[i]) % MOD * fact[j] % MOD);
                ans += ways_left[n - 1 - j] * a[i] % MOD * pow_10[k] % MOD * calc(j, k, dp, len[i]) % MOD * fact[j] % MOD;
                ans %= MOD;
            }
        }
    }
    return ans;
}

int main(){
    init(210);
    int n;
    while(cin >> n){
        vector<ll> a(n);
        vector<ll> len(n);
        vector<ll> b;
        vector<ll> len2;
        REP(i, n){
            string s;
            cin >> s;
            len[i] = s.size();
            a[i] = atoi(s.c_str());
            if(a[i] != 0){
                b.push_back(a[i]);
                len2.push_back(len[i]);
            }
        }
        if(a.size() == b.size()){
            cout << solve(a, len) << endl;
        }else{
            cout << (solve(a, len) - solve(b, len2) + 2 * MOD) % MOD << endl;
        }
    }
    return 0;
}

