llint solve(array<llint,5>in){
	static llint dp[201][201];
	static llint ep[201][201];
	dp[0][0]=1;ep[0][0]=1;
	int h,i=0,j,k,aa=1;
	for(h=1;h<=4;h++){
		aa*=10;
		for(k=1;k<=in[h];k++){
			i++;
			dp[i][0]=dp[i-1][0];
			ep[i][0]=ep[i-1][0];
			dp[i-1][i]=0;ep[i-1][i]=0;
			for(j=1;j<=i;j++){
				dp[i][j]=(dp[i-1][j]+dp[i-1][j-1]*j)%mod;
				ep[i][j]=(ep[i-1][j]+ep[i-1][j-1]*j*aa)%mod;
			}
		}
		//cerr<<"de"<<__LINE__<<endl;
	}
	int n=in[1]+in[2]+in[3]+in[4];
	//cerr<<"n="<<n<<endl;
	llint ue[201]={};
	llint st[201]={};
	for(i=0;i<=n;i++){
		//cerr<<"de"<<__LINE__<<endl;
		for(j=0;j<=i;j++){ue[i]+=dp[i][j];}
		ue[i]%=mod;
		//cerr<<"i="<<i<<"ue="<<ue[i]<<"st="<<st[i]<<endl;
	}
	llint ans=0;
	for(i=0;i<=n;i++){ans+=ue[i]*ep[n][n-i];ans%=mod;}
	//cerr<<"ans="<<ans<<endl;
	return ans;

}

int main(void){
	cout<<fixed<<setprecision(20);cin.tie(0);ios::sync_with_stdio(false);

	llint n;cin>>n;
	//
	//
	// 1,2,3,44
	// m->dp
	//0 
	array<llint,5>mai={},suu={};
	bool Oaru=false;
	int h,i,j;
	for(i=0;i<n;i++){
		int a;cin>>a;
		if(a==0){mai[1]++;Oaru=true;}
		int k=1,kk=0;
		while(k<=a){k*=10;kk++;}
		mai[kk]++;suu[kk]+=a;
	}
	llint ans=0;
	for(h=1;h<=4;h++){
		if(mai[h]==0){continue;}
		mai[h]--;
		ans+=solve(mai)*suu[h];
		if(Oaru&&mai[1]>0){
			mai[1]--;
			ans-=solve(mai)*suu[h];
			mai[1]++;
		}
		ans%=mod;
		mai[h]++;
	}
	ans%=mod;ans+=mod;ans%=mod;
	cout<<ans<<endl;
	return 0;
}

#include <vector>
