					addElem %= MOD;
					ret += addElem;
				}
				ret %= MOD;
			}
		}
		return (int) ret;
	}

	static int[][] calcComb(int[] digits, int cur) {
		digits[cur]--;
		int[][] dp = new int[N][total + 1]; // dp[i][j] : j digits with i cards
		dp[0][0] = 1;
		int sum = 0;
		int idx = 0;
		for (int i = 1; i <= 4; ++i) {
			for (int j = 0; j < digits[i]; ++j, ++idx) {
				sum += i;
				for (int k = idx + 1; k > 0; --k) {
					for (int l = sum; l >= i; --l) {
						dp[k][l] += dp[k - 1][l - i];
						if (dp[k][l] >= MOD) dp[k][l] -= MOD;
					}
				}
			}
		}
		digits[cur]++;
		return dp;
	}
}

