void solve()
{
    ll N, M;
    cin >> N;
    vector<tll> phrase;
    for1 (i, N) {
        ll s, l, p;
        cin >> s >> l >> p;
        phrase.emplace_back(s, l, p);
    }
    cin >> M;
    vll W(M);
    vin(W, M);
    ll wmax = 0;
    for1 (i, M) {
        chmax(wmax, W[i]);
    }
    for1 (w, wmax + 1) {
        dp[w] = -INF;
    }
    dp[0] = 0;
    for1 (w, wmax + 1) {
        for1 (ph, N) {
            ll s, l, p;
            tie(s, l, p) = phrase[ph];
            for2 (j, s, l + 1) {
                if (w - j >= 0) {
                    chmax(dp[w], dp[w - j] + p);
                }
            }
        }
    }
    for1 (i, M) {
        if (dp[W[i]] <= 0) {
            print(-1);
            return;
        }
    }
    for1 (i, M) {
        print(dp[W[i]]);
    }
}

