void func(int x,int len,int point){
  if(x==n){
    int suma=0,sumb=0;
    for(int i=0;i<n;i++)
      if(used[i]) suma+=s[i],sumb+=l[i];
    if(suma<=w&&w<=sumb) a=max(a,point);
    return ;
  }
  func(x+1,len,point);
  if(len+s[x]>w) return;
  flag2=1;
  used[x]=true;
  func(x+1,len+s[x],point+p[x]);
  used[x]=false;
}

