void solve()
{
	READ(int, n);
	auto phrases = read<int>(n,3);

	auto dp = vector<int>(394);
	for (int i = 0; i < dp.size(); i++)
	{
		for (auto &p : phrases)
		{
			for (int j = p[0]; j < p[1]; j++)
			{
				if (i + j < dp.size())
					dp[i + j] = max(dp[i + j], dp[i] + p[2]);
			}
		}
	}

	auto melos = readjag<int>();

	if (all_of(allof(melos), PREDICATE(int,x, dp[x])))
	{
		for (auto x : melos)
		{
			WRITE(dp[x]);
		}
	}
	else
	{
		WRITE(-1);
	}
}

