	while(!deq.empty()&&dp[bi][deq.back()]<=dp[bi][key])deq.pop_back();
	deq.push_back(key);
      }
      while(!deq.empty()&&deq.front()<j-b[i])deq.pop_front();
      if(!deq.empty())dp[ai][j]=max(dp[ai][j],dp[bi][deq.front()]+c[i]);
    }
  }

  int q,Q;
  cin>>Q;
  while(Q--){
    cin>>q;
    cout<<dp[1-n%2][q]<<endl;
  }
  return 0;
}

