#define MAX 393
using namespace std;

struct state{
	state(int a, int b, int c):down(a), up(b), point(c){}
	int down;
	int up;
	int point;
};

int main(){
	int n = 0;
	cin >> n;
	vector<int> table(MAX+1, -1);
	state combi(0, 0, 0);
	for(int i = 0; i < n; i++){
		state input(0, 0, 0);
		cin >> input.down >> input.up >> input.point;
		state incopy = input;
		while(incopy.down < MAX){
			if(incopy.up > MAX) incopy.up = MAX;
			for(int j = incopy.down; j <= incopy.up; j++){
				if(table[j]<incopy.point)table[j] = incopy.point; 
			}
			state ccopy = combi;
			while(ccopy.down&&incopy.down+ccopy.down < MAX){
				if(incopy.up+ccopy.up > MAX) ccopy.up = MAX-incopy.up;
				for(int j = incopy.down+ccopy.down; j <= incopy.up+ccopy.up; j++){
					if(table[j]<incopy.point+ccopy.point)table[j] = incopy.point+ccopy.point; 
				}
				ccopy.down += combi.down;
				ccopy.up += combi.up;
				ccopy.point += combi.point;
			}
			incopy.down += input.down;
			incopy.up += input.up;
			incopy.point += input.point;
		}
		combi.down += input.down;
		combi.up += input.up;
		combi.point += input.point;
	}

	queue<int> q;
	int m = 0;
	cin >> m;
	for(int i = 0; i < m; i++){
		int tmp = 0;
		cin >> tmp;
		if(table[tmp]!=-1) q.push(table[tmp]);
		else{
			cout << "-1" << endl;
			return 0;
		}
	}
	while(!q.empty()){
		cout << q.front() << endl;
		q.pop();
	}

	return 0;
}

