using namespace std;
static const int MAX = 500;

int n;
int s[MAX], l[MAX], p[MAX];

int solve(int w){
  int dp[MAX];
  for ( int i = 0; i <= w; i++ ) dp[i] = -1;
  dp[0] = 0;
  for ( int i = 0; i <= w; i++ ){
    if ( dp[i] == -1 ) continue;
    for ( int k = 0; k < n; k++ ){
      for ( int j = i+s[k]; j <= i+l[k]; j++ ){
	if ( j > w ) continue;
	dp[j] = max(dp[j], dp[i] + p[k]);
      }
    }
  }
  return dp[w];
}

main(){
  int m, w;
  vector<int> ans;
  cin >> n;
  for ( int i = 0; i < n; i++ ) cin >> s[i] >> l[i] >> p[i];
  cin >> m;
  for ( int i = 0; i < m; i++ ){
    cin >> w;
    int o = solve(w);
    if ( o == -1 ){
      cout << -1 << endl; return 0;
    }
    ans.push_back(o);
  }
  for ( int i = 0; i < m; i++ ) cout << ans[i] << endl;

}

