void func(int x,int len,int point){
  if(x==n){
    a=max(a,point);
    return ;
  }
  func(x+1,len,point);
  for(int i=0;i<n;i++)
    for(int j=s[i];j<=l[i];j++){
      if(len+j>w) continue;
      flag2=1;
      func(x+1,len+j,point+p[i]);
    }
}

