#include <iostream>
#include <algorithm>
using namespace std;

struct data{
  int s,l,p;
  data(int s=0, int l=0, int p=0):s(s),l(l),p(p){}
};

int n,dp[2][394];
data v[393];

int solve(int w){
  fill(dp[0],dp[2],0);

  for(int i=0;i<n;i++){
    for(int j=v[i].s;j<=v[i].l;j++){
      if(j > w) break;
      for(int k=j;k<=w;k++) dp[1][k] = max(dp[1][k],dp[0][k-j]+v[i].p);
    }
    for(int j=0;j<=w;j++) dp[0][j] = dp[1][j];
  }

  return dp[1][w];
}

int main(){
  while(cin >> n){
    for(int i=0;i<n;i++) cin >> v[i].s >> v[i].l >> v[i].p;
    int m,w,ans[393];
    bool f = false;
    cin >> m;
    for(int i=0;i<m;i++){
      cin >> w;
      if(f) continue;
      ans[i] = solve(w);
      if(ans[i] == 0) f = true;
    }
    if(f) cout << -1 << endl;
    else for(int i=0;i<m;i++) cout << ans[i] << endl;
  }
}

