const int MOD = 998244353;
const int INF = 100000002;
const double PI =4*atan(1);
const double eps = 1e-7;
const long long oo = 1e18;

int solver(const int N, const int M, vector<int> &S, vector<int> &L, vector<int> &P){
  vector<vl> dp(N+1, vl((M+1),0));

  rep(i,N){
    rep(j,M+1){
      if(j<S[i]){
        dp[i+1][j] = max(dp[i][j],dp[i+1][j]);
        continue;
      }else{
        dp[i+1][j] = max({dp[i][j],dp[i+1][j-S[i]]+P[i],dp[i+1][max(0,j-L[i])]+P[i]});
      } 
    } 
  }
  return dp[N][M];
}



int main(){
  int N;
  cin >> N;
  vector<int> S(N), L(N), P(N);
  rep(i, N) cin >> S[i] >> L[i] >> P[i];
  int M;
  cin >> M;
  bool check=false;
  vector<int> ans(M);
  rep(i,M){
    int m;
    cin >> m;
    int a = solver(N,m,S,L,P);
    if(a<=0){
      cout << -1 << endl;
      return 0;
    }else ans[i]=a;
  }
  rep(i,M){
    cout << ans[i] << endl;
  }
}

