#include <bits/stdc++.h>

using namespace std;

struct RangeSegmentTree
{
  vector< int > data;
  int sz;

  RangeSegmentTree(int n)
  {
    sz = 1;
    while(sz < n) sz <<= 1;
    data.assign(2 * sz - 1, -1);
  }

  void update(int a, int b, int x, int k, int l, int r)
  {
    if(a >= r || b <= l) {
      return;
    } else if(a <= l && r <= b) {
      data[k] = max(data[k], x);
    } else {
      update(a, b, x, 2 * k + 1, l, (l + r) >> 1);
      update(a, b, x, 2 * k + 2, (l + r) >> 1, r);
    }
  }

  void update(int a, int b, int x)
  {
    update(a, b, x, 0, 0, sz);
  }

  int get(int k)
  {
    k += sz - 1;
    int ret = data[k];
    while(k > 0) {
      k = (k - 1) >> 1;
      ret = max(ret, data[k]);
    }
    return (ret);
  }
};

int main()
{
  int N, M;
  RangeSegmentTree tree(394);
  int dp[394];

  cin >> N;
  while(N--) {
    int s, l, p;
    cin >> s >> l >> p;
    tree.update(s, l + 1, p);
  }
  for(int i = 0; i < 394; i++) {
    dp[i] = tree.get(i);
  }

  for(int i = 0; i < 394; i++) {
    if(dp[i] == -1) continue;
    for(int j = 0; j <= i && i + j < 394; j++) {
      if(dp[j] == -1) continue;
      dp[i + j] = max(dp[i + j], dp[i] + dp[j]);
    }
  }
  cin >> M;
  vector< int > q;
  while(M--) {
    int w;
    cin >> w;
    if(~dp[w])  {
      q.emplace_back(dp[w]);
    } else {
      cout << -1 << endl;
      exit(0);
    }
  }
  for(int k : q) {
    cout << k << endl;
  }
}

