                            DP[j][k] = max(DP[j-1][k], max(DP[j][k], DP[j][k-a] + p[j]));
                            break;
                        }
                        else DP[j][k] = DP[j-1][k];
                    }
                }
                else{
                    DP[j][k] = DP[j-1][k];
                }
            }
        }
        if(DP[N-1][w[i]]==-1) can = false;
        else ans[i] = DP[N-1][w[i]];
