void set(int s, int d, int p){
	if(393 < s) return;
	if(dppnt[s] < p){
		if(dpdiv[s] > d) set(s + d, dpdiv[s] - d, dppnt[s]);
		dppnt[s] = p;
		dpdiv[s] = d;
	}else{
		if(dpdiv[s] < d) set(s + dpdiv[s], d - dpdiv[s], p);
	}
}

main(){
	int i,j;
	cin >> n;
	while(n--){
		int s,d,p;
		cin >>s>>d>>p;
		for(i=393;i;i--) if(dppnt[i]) set(i+s, dpdiv[i] + d-s, p+dppnt[i]);
		set(s, d-s, p);
	}
	for(i=0;i<394;i++){
		for(j=1;j<=dpdiv[i];j++){
			set(i+j, dpdiv[i]-j, dppnt[i]);
		}
	}
	cin >> n;
	vector<int> ans;
	while(n--){
		int m;
		cin >> m;
		if(!dppnt[m]){
			cout << "-1" << endl;
			return 0;
		}
