// Geometry

#define EPS 0.0000000001

class Vector2 {
public:
  double x, y;

  Vector2(double x = 0, double y = 0): x(x), y(y) {}

  Vector2 operator + (Vector2 v) { return Vector2(x + v.x, y + v.y); }
  Vector2 operator - (Vector2 v) { return Vector2(x - v.x, y - v.y); }
  Vector2 operator * (double k) { return Vector2(x * k, y * k); }
  Vector2 operator / (double k) { return Vector2(x / k, y / k); }

  double length() { return sqrt(norm()); }
  double norm() { return x * x + y * y; }
  double dot (Vector2 v) { return x * v.x + y * v.y; }
  double cross (Vector2 v) { return x * v.y - y * v.x; }

  bool operator < (const Vector2 &v) const {
    return x != v.x ? x < v.x : y < v.y;
  }

  bool operator == (const Vector2 &v) const {
    return fabs(x - v.x) < EPS && fabs(y - v.y) < EPS;
  }
};

ostream & operator << (ostream & out, Vector2 const & v) { 
  out<< "Vector2(" << v.x << ", " << v.y << ')';
  return out;
}

istream & operator >> (istream & in, Vector2 & v) { 
  double x, y;
  in >> x;
  in >> y;
  v.x = x;
  v.y = y;
  return in;
}

struct Phrase {
  Int l, p;

  bool operator < (const Phrase &other) const {
    return (l == other.l) ? p < other.p : l < other.l;
  }
};

#define MAX_W 394
#define MAX_N 394
Int N, M;

vector<Phrase> phrases_;
vector<Phrase> phrases;
vector<Int> melos;


void input() {
  Int s, l, p;
  Phrase ph;
  cin >> N;
  rep(N) {
    cin >> s >> l >> p;
    loop(x,s,l+1) {
      ph.l = x, ph.p = p;
      phrases_.push_back(ph);
    }
  }
  cin >> M;
  rep(M) {
    cin >> s;
    melos.push_back(s);
  }

  sort(span_all(phrases_));
  reverse(span_all(phrases_));
  Int prev_l = -1;
  for (auto ph: phrases_) {
    if (prev_l == ph.l) continue;
    phrases.push_back(ph);
    prev_l = ph.l;
  }
  N = phrases.size();

  phrases_.clear();
}

vector<vector<Int> > dp(MAX_N, vector<Int>(MAX_W, -1));

Int dfs(Int n, Int cap) {
  if (n == N || cap == 0) return 0;
  if (dp.at(n).at(cap) >= 0) return dp.at(n).at(cap);
  Int val = dfs(n + 1, cap);
  Phrase ph = phrases.at(n);
  if (cap >= ph.l) {
    val = max(
      val,
      max(
        ph.p + dfs(n + 1, cap - ph.l),
        ph.p + dfs(n, cap - ph.l)
      )
    );
  }
  dp.at(n).at(cap) = val;
  return val;
}

void solve() {
  vector<Int> ans;
  for (auto melo: melos) {
    loop(i,0,MAX_N) loop(j,0,MAX_W) dp.at(i).at(j) = -1;
    Int score = dfs(0, melo);
    if (score == 0) {
      cout << -1 << endl;
      return;
    }
    ans.push_back(score);
  }

  for (auto a: ans) cout << a << endl;
}

int main() {
  input();
  solve();
  return 0;
}

