struct P {int x,y,z;};
typedef pair<double,int> PP;

int main() {
  int n;
  cin >> n;
  P p[n];
  PP a[n];
  for(int i=0; i<n; i++) {
    int x,y,z;
    cin >> x >> y >> z;
    p[i]=(P){x,y,z};
    a[i]=PP((double)z/x,i);
  }
  sort(a,a+n,greater<PP>());
  vector<int> ans;
  int c[400];
  memset(c,0,sizeof(c));
  for(int i=0; i<400; i++) {
    int r=i;
    for(int k=0; k<n; k++) {
      while(r>=p[a[k].second].x) {
        r-=p[a[k].second].x;
        c[i]+=p[a[k].second].z;
      }
    }
    if(r) c[i]=-1;
  }
  int m;
  cin >> m;
  while(m--) {
    int M=-1,e;
    cin >> e;
    for(int i=0; i<n; i++) {
      for(int j=p[i].x; j<=p[i].y; j++) {
        int r=e-j,d=p[i].z;
        if(r<0) continue;
        else if(c[r]!=-1) M=max(M,d+c[r]);
      }
    }
    if(M==-1) {
      cout << -1 << endl;
      return 0;
    } else ans.push_back(M);
  }
  for(int i=0; i<ans.size(); i++) cout << ans[i] << endl;
  return 0;
}

