int main(){
  int n;
  scanf("%d",&n);
  std::vector<int > s(n), l(n), p(n);
  for (int i=0; i<n; i++){
    scanf("%d",&s[i]);
    scanf("%d",&l[i]);
    scanf("%d",&p[i]);
  }
  int m;
  scanf("%d",&m);
  std::vector<int > w(m);
  for (int i=0; i<m; i++){
    scanf("%d",&w[i]);
  }
  std::vector<std::vector<int > > dp(n, std::vector<int > (394, 0));

  // i=0 case
  for (int j=0; j<=393; j++){
    if (j<s[0]){
      dp[0][j] = 0;
    } else {
      for (int k=s[0]; k<=l[0]; k++){
        dp[0][j] = std::max(dp[0][std::max(j-k,0)]+p[0], dp[0][j]);
      }
    }
  }

  // i>0 case
  for (int i=1; i<n; i++){
    for (int j=0; j<=393; j++){
      if (j<s[i]){
        dp[i][j] = dp[i-1][j];
      } else {
        for (int k=s[i]; k<=l[i]; k++){
          dp[i][j] = std::max(dp[i][std::max(j-k,0)]+p[i], dp[i][j]);
        }
        dp[i][j] = std::max(dp[i-1][j], dp[i][j]);
      }
    }
  }

  // answer
  bool ng = false;
  for (int i=0; i<m; i++){
    if (dp[n-1][w[i]]==0){
      ng = true;
    }
  }

  if (ng) {
    printf("-1\n");
  } else {
    for (int i=0; i<m; i++){
      printf("%d\n",dp[n-1][w[i]]);
    }
  }
  return 0;
}

