#define M_MAX     (393)
#define N_MAX     (393)
#define LEN_MAX   (393)
typedef pair<DWORD, DWORD> PAIR;

int N, M;
DWORD Dp[N_MAX + 1][LEN_MAX + 1] = {};
DWORD adwMelody[M_MAX] = {};
DWORD adwPoint[N_MAX] = {};
vector<PAIR> vFraseLength;


void input()
{
    cin >> N;
    REP(i, 0, N) {
        PAIR tempLength;
        cin >> tempLength.first >> tempLength.second;
        vFraseLength.push_back(tempLength);
        cin >> adwPoint[i];
    }

    cin >> M;
    REP(i, 0, M) {
        cin >> adwMelody[i];
    }

}

void CalcDpTable()
{
    REP(i, 0, N) {
        PAIR nowFrase = vFraseLength[i];
        DWORD   nowPoint = adwPoint[i];
        REP(j, 0, LEN_MAX + 1) {
            if (j >= nowFrase.first) {
                DWORD tmpVal = Dp[i][j];
                REP(k, nowFrase.first, nowFrase.second+1) {
                    if (j < k) break;
                    tmpVal = max(tmpVal, max(Dp[i][j-k] + nowPoint, Dp[i+1][j-k] + nowPoint));
                }
                Dp[i+1][j] = tmpVal;
            } else {
                Dp[i + 1][j] = Dp[i][j];
            }
        }
    }
    return;
}

void makeAns()
{
    vector<DWORD> ans;
    REP(i, 0, M) {
        DWORD tmpAns = Dp[N][adwMelody[i]];

        if (tmpAns == 0) {
            cout << -1 << endl;
            return;
        } else {
            ans.push_back(tmpAns);
        }
    }

    REP(i, 0, M) {
        cout << ans[i] << endl;
    }
    return;
}

void solve_main()
{
    input();

    CalcDpTable();

    makeAns();
}

