using namespace std;

class dp{
public:
  int n;
  static const int wlimit=10;
  int *s, *l, *p;
  int (*d)[wlimit+1];
  bool (*can)[wlimit+1];
  dp(int n):n(n){
    s = new int[n+1];
    l = new int[n+1];
    p = new int[n+1];
    d = new int[n+1][wlimit+1];
    can = new bool[n+1][wlimit+1];
  }
  ~dp(){
    delete[] s;
    delete[] l;
    delete[] p;
    delete[] d;
  }

  void init(){
    for(int i=0; i<=n; i++){
      for(int j=0; j<=wlimit; j++) {
        d[i][j] = 0;
        if(j==0) can[i][j] = true;
        else can[i][j] = false;
      }
    }
    for(int i=1; i<=n; i++){
      for(int j=0; j<=wlimit; j++){
        int temp=0;
        for(int k=s[i]; k<=l[i]; k++){
          if(j<k) temp = max(temp, d[i-1][j]);
          else {
            if(can[i][j-k]){
              temp = max(temp, max(d[i-1][j], d[i][j-k]+p[i]));
              can[i][j] = true;
            }
          }
        }
        d[i][j] = temp;
      }
    }
  }

  vector<int> ans(vector<int> w){
    vector<int> ans;
    for(int i=0; i<w.size(); i++){
      if(d[n][w[i]]==0){
        ans = vector<int>(1, -1);
        return ans;
      }
      ans.push_back(d[n][w[i]]);
    }
    return ans;
  }
};

int main(){
  int n; cin >> n;
  dp d(n);

  int s, l, p;
  for(int i=1; i<=n; i++){
    cin >> s >> l >> p;
    d.s[i] = s;
    d.l[i] = l;
    d.p[i] = p;
  }

  d.init();

  int m; cin >> m;
  vector<int> melodies;
  for(int i=0; i<m; i++){
    int w; cin >> w;
    melodies.push_back(w);
  }

  vector<int> ans = d.ans(melodies);
  for(int i=0; i<ans.size(); i++){
    cout << ans[i] << endl;
  }

  return 0;
}

