static inline void inputString(char *pcStr)
{
    char *pcCur = pcStr;
    for (;;) {
        char c = getchar();
        if (('\n' == c) || (EOF == c)) {
            break;
        }
        *pcCur = c;
        pcCur++;
    }
    *pcCur = '\0';
}


static inline SQWORD inputSQWORD(void)
{
    SQWORD sqNumber = 0;
    SQWORD sqMultiplier = 1;
    M_BOOL bRead = M_FALSE;
    for (;;) {
        char c = getchar();
        if (!bRead) {
            if ('-' == c) {
                sqMultiplier = -1;
            }
        }
        if (('0' <= c) && (c <= '9')) {
            sqNumber *= 10LL;
            sqNumber += (SQWORD)(c - '0');
            bRead = M_TRUE;
        } else {
            if (bRead) {
                return sqNumber * sqMultiplier;
            }
        }
    }
}


static inline SDWORD inputSDWORD(void)
{
    SDWORD lNumber = 0;
    SDWORD lMultiplier = 1;
    M_BOOL bRead = M_FALSE;
    for (;;) {
        char c = getchar();
        if (!bRead) {
            if ('-' == c) {
                lMultiplier = -1;
            }
        }
        if (('0' <= c) && (c <= '9')) {
            lNumber *= 10;
            lNumber += (c - '0');
            bRead = M_TRUE;
        } else {
            if (bRead) {
                return lNumber * lMultiplier;
            }
        }
    }
}

static inline DOUBLE inputFP(void)
{
    DOUBLE dInt = 0.0;
    DOUBLE dFrac = 0.0;
    DOUBLE dMultiplier = 1.0;
    DWORD dwFpCnt = 0;
    DOUBLE *pdCur = &dInt;
    M_BOOL bRead = M_FALSE;
    for (;;) {
        char c = getchar();
        if (!bRead) {
            if ('-' == c) {
                dMultiplier = -1;
            }
        }
        if ('.' == c) {
            pdCur = &dFrac;
        } else if (('0' <= c) && (c <= '9')) {
            (*pdCur) *= 10;
            (*pdCur) += (DOUBLE)(c - '0');
            bRead = M_TRUE;
            if (pdCur == &dFrac) {
                dwFpCnt++;
            }
        } else {
            if (bRead) {
                return dMultiplier * (dInt + dFrac / (pow((DOUBLE)10.0 , (DOUBLE)dwFpCnt)));
            }
        }
    }
}

struct PHRASE {
    SDWORD lLower;
    SDWORD lUpper;
    SDWORD lPoint;
};

#define MAX_MUSIC_LEN   (393)
#define MAX_LOOP        (MAX_MUSIC_LEN)
#define MAX_PNT         (393)
static SDWORD getDpTbl(
    const vector<SDWORD> &vlPnts, 
    SDWORD lMinLen,
    vector<SDWORD> &vDpTbl)
{

    /* dp:長さ i を実現する最大の得点 */
    SDWORD lMaxLoop = (MAX_MUSIC_LEN + lMinLen - 1) / lMinLen;
    for (SDWORD lLoopIdx = 0; lLoopIdx < lMaxLoop; lLoopIdx++) {
        for (SDWORD lDpTblIdx = vDpTbl.size(); 0 <= lDpTblIdx; lDpTblIdx--) {
            SDWORD lMaxPnt = 0;
            for (SDWORD lPrevDpIdx = 0; lPrevDpIdx < lDpTblIdx; lPrevDpIdx++) {
                SDWORD lPnt = vDpTbl[lPrevDpIdx] + vlPnts[lDpTblIdx - lPrevDpIdx];
                lMaxPnt = max(lMaxPnt, lPnt);
            }
            vDpTbl[lDpTblIdx] = lMaxPnt;
        }
    }
    return 0;
}


#define MAX_PHRASE_LEN  (393)
int main()
{
    SDWORD lNumPhrase = inputSDWORD();
    vector<PHRASE>     vPhrases;
    for (SDWORD lIdx = 0; lIdx < lNumPhrase; lIdx++) {
        PHRASE stPhraseOne;
        stPhraseOne.lLower = inputSDWORD();
        stPhraseOne.lUpper = inputSDWORD();
        stPhraseOne.lPoint = inputSDWORD();

        vPhrases.emplace_back(stPhraseOne);
    }
//    printf("A\n");

    /* 長さに対する最大のポイントを計算する */
    vector<SDWORD> vlPnts(MAX_PHRASE_LEN + 1, 0);
    for (auto phrase: vPhrases) {
        for (SDWORD lLen = phrase.lLower; lLen <= phrase.lUpper; lLen++) {
            vlPnts[lLen] = max(vlPnts[lLen], phrase.lPoint);
        }
    }
//    printf("B\n");

    SDWORD lMinLen = MAX_MUSIC_LEN;
    for (auto phrase: vPhrases) {
        lMinLen = min(lMinLen, phrase.lLower);
    }

    vector<SDWORD> vDpTbl(MAX_MUSIC_LEN, 0);
    getDpTbl(vlPnts, lMinLen, vDpTbl);

    SDWORD lNumMusic = inputSDWORD();
    vector<SDWORD> vAns;
    for (SDWORD lMusicIdx = 0; lMusicIdx < lNumMusic; lMusicIdx++) {
        SDWORD lMusicLen = inputSDWORD();

        SDWORD lPnt = vDpTbl[lMusicLen];
        vAns.emplace_back(lPnt);

        if (0 == lPnt) {
            printf("-1\n");
            return 0;
        }
    }
    for (auto pnt:vAns) {
        printf("%d\n", pnt);
    }

    return 0;
}

