int rec(int i, int j){
  if(dp[i][j] >= 0) return dp[i][j];
  int res;
  if(i == MIKU*10+4 || j == 0) res = 0;
  else if(j < i) res = rec(i + 1, j);
  else  res = max(rec(i + 1, j), rec(i + 1, j - i) + p[i]);

  return dp[i][j] = res;
}

