/* constant */

const int MAX_N = 393;
const int MAX_M = 393;
const int MAX_W = 393;

/* typedef */

typedef vector<int> vi;

/* global variables */

vi cs, ps, ans;
int wis[MAX_M + 1], dp[2][MAX_W + 1];

/* subroutines */

/* main */

int main() {
  int n;
  cin >> n;

  for (int i = 0; i < n; i++) {
    int si, li, pi;
    cin >> si >> li >> pi;
    for (int j = si; j <= li; j++)
      cs.push_back(j), ps.push_back(pi);
  }
  n = cs.size();

  int m, maxw = 0;
  cin >> m;

  for (int i = 0; i < m; i++) {
    cin >> wis[i];
    if (maxw < wis[i]) maxw = wis[i];
  }

  memset(dp[0], -1, sizeof(dp[0]));
  dp[0][0] = 0;
  int cur = 0, nxt = 1;

  for (int i = 0; i < n; i++) {
    memcpy(dp[nxt], dp[cur], sizeof(dp[0]));

    int &ci = cs[i], c = ci;
    int &pi = ps[i], p = pi;

    while (c <= maxw) {
      int max_j = maxw - c;
      for (int j = 0; j <= max_j; j++)
        if (dp[cur][j] >= 0) {
          int d = dp[cur][j] + p;
          if (dp[nxt][j + c] < d) dp[nxt][j + c] = d;
        }
      c += ci;
      p += pi;
    }
    cur ^= 1; nxt ^= 1;
  }

  bool ok = true;

  for (int i = 0; i < m; i++)
    if (dp[cur][wis[i]] <= 0) {
      ok = false;
      break;
    }

  if (ok)
    for (int i = 0; i < m; i++) printf("%d\n", dp[cur][wis[i]]);
  else
    puts("-1");

  return 0;
}

