using namespace std;
const int MAX = 394; 
int dp[394][394];

void DP(int amount, int sq, int v){
	for(int j = 1; j < MAX; j++){
		if(amount > j) dp[sq][j] = dp[sq-1][j]<dp[sq][j]?dp[sq][j]:dp[sq-1][j];
		else{
			if(!dp[sq-1][j-amount]) continue;
			int tmp = dp[sq-1][j]<dp[sq-1][j-amount]+v ? dp[sq-1][j-amount]+v : dp[sq-1][j];
			dp[sq][j] = tmp<dp[sq][j] ? dp[sq][j] : tmp;
		}
	}
}

int main(){
	int n;
	cin >> n;
	for(int i = 0; i <= n; i++) fill(dp[i], dp[i]+MAX, 0);
	int sq = 0;
	while(n--){
		sq++;
		int up, down, v;
		cin >> down >> up >> v;
		while(down <= MAX){
			if(up>=MAX) up = MAX-1;
			for(int i = down; i <= up; i++){
				int tmp = dp[sq-1][i]<v?v:dp[sq-1][i];
				dp[sq][i] = dp[sq][i]<v?v:dp[sq][i];
			}
			for(int i = 0; i < 2; i++){
				if(i) DP(up, sq, v);
				else DP(down, sq, v);
			}
			up += up;
			down += down;
		}
	}
	int m;
	cin >> m;
	while(m--){
		int input;
		cin >> input;
		if(dp[sq][input]) cout << dp[sq][input] << endl;
		else cout << "-1" << endl;
	}
	return 0;
}

