#include <iostream>
#include <algorithm>
#include <vector>
#include <cstring>
using namespace std;

class dp{
public:
  int n;
  static const int limit=393;
  int s[limit+1], l[limit+1], p[limit+1];
  int d[limit+1][limit+1];
  bool can[limit+1][limit+1];

  dp(int n):n(n){}

  void init(){
    for(int i=0; i<=n; i++){
      for(int j=0; j<=limit; j++) {
        d[i][j] = 0;
        if(j==0) can[i][j] = true;
        else can[i][j] = false;
      }
    }
    for(int i=1; i<=n; i++){
      for(int j=0; j<=limit; j++){
        int temp=0;
        for(int k=s[i]; k<=l[i]; k++){
          if(can[i-1][j]){
            temp = max(temp, d[i-1][j]);
            can[i][j] = true;
          }
          if(j>=k) {
            if(can[i][j-k]){
              temp = max(temp, d[i][j-k]+p[i]);
              can[i][j] = true;
            }
          }
        }
        d[i][j] = temp;
      }
    }
  }

  vector<int> ans(vector<int> w){
    vector<int> ans;
    for(int i=0; i<w.size(); i++){
      if(!can[n][w[i]]){
        ans = vector<int>(1, -1);
        return ans;
      }
      ans.push_back(d[n][w[i]]);
    }
    return ans;
  }
};

int main(){
  int n; cin >> n;
  dp d(n);

  int s, l, p;
  for(int i=1; i<=n; i++){
    cin >> s >> l >> p;
    d.s[i] = s;
    d.l[i] = l;
    d.p[i] = p;
  }

  d.init();

  int m; cin >> m;
  vector<int> melodies;
  for(int i=0; i<m; i++){
    int w; cin >> w;
    melodies.push_back(w);
  }

  vector<int> ans = d.ans(melodies);
  for(int i=0; i<ans.size(); i++){
    cout << ans[i] << endl;
  }

  return 0;
}

