int set(int s, int d, int p){
	if(dppnt[s] < p){
		if(dpdiv[s] > d) set(s + d, dpdiv[s] - d, dppnt[s]);
		dppnt[s] = p;
		dpdiv[s] = d;
	}else{
		if(dpdiv[s] < d) set(s + dpdiv[s], d - dpdiv[s], p);
	}
}

