int tansaku(int rest) {
	int i,j;
	int result=0;
	int nowscore;
	if(rest==0)return 0;
	if(rest<0)return -10000000;
	if(memo[rest]>0)return memo[rest]-1;
	for(i=0;i<n;i++) {
		for(j=phrase[i].min;j<=phrase[i].max;j++) {
			nowscore=tansaku(rest-j)+phrase[i].score;
			if(result<nowscore)result=nowscore;
		}
	}
	memo[rest]=result+1;
	return result;
}

