#include<stdio.h>
#include<stdlib.h>
typedef struct d{int s,l,p;}d;
d D[400];
int N,M,W,i,j,r[400];
int c(const void *a, const void *b)
{
	d*x=(d*)a;
	d*y=(d*)b;
	return x->s<y->s;
}

int f(int w)
{
	int d=0,i,r=0;
	for(i=0;i<N;)
	{
		if(D[i].s<=w)
		{
			r+=D[i].p;
			d+=D[i].l-D[i].s;
			w-=D[i].s;
		}
		else
			i++;
	}
	return w<=d?r:-1;
}

int main()
{
	scanf("%d",&N);
	for(i=0;i<N;i++)scanf("%d%d%d",&D[i].s,&D[i].l,&D[i].p);

	qsort(D,N,sizeof(d),c);
	scanf("%d",&M);
	for(i=0;i<M;i++)
	{
		scanf("%d",&W);
		r[i]=f(W);
		if(r[i]==-1)
		{
			r[0]=-1;
			break;
		}
	}

	for(i=0;i<M;i++)
	{
		printf("%d\n",r[i]);
		if(r[i]==-1)break;
	}
	return 0;
}

fun main(args:Array<String>?): Unit {
    val n = readLine()!!.trim().toInt()
    val phrase = Array(n){
        val (s, l, p) = readLine()!!.trim().split(' ').map(String::toInt)
        Phrase(s, l, p)
    }
    val length = Array(394){Phrase(0, 0, 0)}
    length[phrase.first().minLength] = phrase.first()
    for (i in 1 until phrase.size){
        val p = phrase[i]
        for (j in 0 .. 393) {
            if (length[j].minLength > 0 || j == 0){
                var new = p + length[j]
                while(new.minLength <= 393) {
                    val old = length[new.minLength]
                    if (new.maxLength >= old.maxLength && new.point >= old.point) {
                        length[new.minLength] = new
                        break
                    } else if (new.maxLength <= old.maxLength && new.point <= old.point) {
                        break
                    }else {
                        length[new.minLength] = Phrase(new.minLength, Math.min(new.maxLength, old.maxLength), Math.max(new.point, old.point))
                        new = Phrase(Math.min(new.maxLength, old.maxLength) + 1, Math.max(new.maxLength, old.maxLength), Math.min(new.point, old.point))
                    }
                }
            }
        }
    }
    val maxPoint = IntArray(394){-1}
    for (p in length) if (p.point > 0){
        for (i in p.minLength .. Math.min(393, p.maxLength)) maxPoint[i] = Math.max(maxPoint[i], p.point)
    }
    println(IntArray(readLine()!!.trim().toInt()){maxPoint[readLine()!!.trim().toInt()]}.takeUnless{it.contains(-1)}?.joinToString("\n") ?: -1)
}
