			for(int j=0;j<=sum;j++){
				if(ans[j]>0){
					for(int k=a[i];k<=b[i];k++){
						if(j+k<=sum) temp[j+k] = Math.max(temp[j+k], ans[j]+c[i]);
					}
				}
			}
			for(int j=a[i];j<=b[i];j++) temp[j] = Math.max(temp[j], c[i]);
			for(int j=0;j<=sum;j++){
				ans[j] = Math.max(ans[j], temp[j]);
				temp[j] = 0;
			}
