			s[i] = sc.nextInt(); l[i] = sc.nextInt(); p[i] = sc.nextInt();
		}
		int[] dp = new int[len+1]; fill(dp, -1); dp[0] = 0;
		for(int j=0;j<=len;j++) for(int i=0;i<n;i++) if(dp[j] >= 0){
			for(int k=s[i];k<=l[i];k++) if(j+k<=len) dp[j+k] = max(dp[j+k], dp[j]+p[i]);
		}

