			int[] dp = new int[w + 1];
			for (Map.Entry<Integer, Integer> p : P.entrySet()) {
				for (int j = p.getKey(); j <= w; j++) {
					dp[j] = Math.max(dp[j], dp[j - p.getKey()] + p.getValue());
				}
			}
			if (dp[w] == 0) {
				notfound = true;
				break;
			}
			ans.append(dp[w]).append('\n');
		}

		if (notfound) {
			System.out.println(-1);
		} else {
			System.out.print(ans.toString());
		}
	}
}

