        for(int i=0; i<394; i++){
            for(int j=0; j<n; j++){
                for(int k=s[j]; k<=l[j]; k++){
                    if(i-k<0) break;
                    if(point[i-k]!=-1) point[i]  = Math.max(point[i], point[i-k] + p[j]);
